import axios from 'axios';
export default {
  name: 'app',
  data: function data() {
    return {
      product: {},
      posts: [],
      errors: []
    };
  },
  created: function created() {
    // Fetches posts when the component is created.
    this.getanimes();
  },
  methods: {
    getanimes: function getanimes() {
      var _this = this;

      axios.get('http://35.227.59.176:8081/api/animes').then(function (response) {
        // JSON responses are automatically parsed.
        _this.posts = response.data;
      }).catch(function (e) {
        _this.errors.push(e);
      });
    },
    getoneproduct: function getoneproduct(idproduct) {
      var _this2 = this;

      axios.get('http://localhost:8081/api/products/' + idproduct).then(function (response) {
        // JSON responses are automatically parsed.
        _this2.product = response.data;
        alert("status: " + response.status + ", selected: " + JSON.stringify(response.data));
      }).catch(function (e) {
        _this2.errors.push(e);
      });
    },
    addProduct: function addProduct() {
      var _this3 = this;

      alert(this.product.codigo + this.product.nombre + this.product.precio + this.product.exist);
      axios.post('http://localhost:8081/api/products', this.product).then(function (response) {
        alert("status: " + response.status + ", inserted: " + JSON.stringify(response.data));
      }).catch(function (e) {
        _this3.errors.push(e);
      });
    },
    updateProduct: function updateProduct() {
      var _this4 = this;

      //alert(idproduct);
      alert(this.product.id);
      axios.put('http://localhost:8081/api/products/' + this.product.id, this.product).then(function (response) {
        alert("status: " + response.status + ", updated: " + JSON.stringify(response.data));

        _this4.getproducts();
      }).catch(function (e) {
        _this4.errors.push(e);
      });
    },
    deleteProduct: function deleteProduct(idproduct) {
      var _this5 = this;

      alert("delete" + idproduct);
      axios.delete('http://localhost:8081/api/products/' + idproduct).then(function (response) {
        alert("status: " + response.status + ", deleted: " + JSON.stringify(response.data));

        _this5.getproducts();
      }).catch(function (e) {
        _this5.errors.push(e);
      });
    }
  }
};
